webpackJsonp([0xa1183948883c],{5878:function(o,t){o.exports={data:{post:{html:'<p>Update: Mark has now <a title="How to hide CSS from Safari: solution [dive into mark]" href="http://diveintomark.org/archives/2003/01/12.html#how_to_hide_css_from_safari_solution">found</a> a bug to hide CSS from only Safari. He\'s not quite correct, of course, since the bug also hides CSS from <a title="Konqueror" href="http://www.konqueror.org/">Konqueror</a>, which also uses KHTML. But then he tested on only <acronym title="Windows, Macintosh">devilish operating systems</acronym>, and not on systems that are <acronym title="Linux, BSD">good and pure</acronym>.</p>',fields:{title:"Buggy Browsers Deux",date:"13 Jan 2003",url:"https://papascott.de/2003-01-13-buggy-browsers-deux/"}}},pathContext:{slug:"/2003-01-13-buggy-browsers-deux/",prev:{fields:{title:"Buggy Browsers",date:"10 Jan 2003",slug:"/2003-01-10-buggy-browsers/"}},next:{fields:{title:"To Book or Not To Book",date:"13 Jan 2003",slug:"/2003-01-13-to-book-or-not-to-book/"}}}}}});
