webpackJsonp([64361220539168],{6547:function(t,e){t.exports={data:{post:{html:'<p>Having been inspired by Simon Williston\'s post on using a wiki for <a title="Simon Willison: Simpler content managment" href="http://simon.incutio.com/archive/2003/12/05/simple">simpler content managment</a>, I\'ve set up a <a title="PapaScottWiki" href="http://www.papascott.de/tavi/">read-only wiki</a> of my own as a public notebook for my work. There\'s not much there yet, but maybe I can all my technical-oriented posts there, thus saving this space for important stuff like Christopher pictures.</p>',fields:{title:"Wimpy Wiki",date:"10 Dec 2003",url:"https://papascott.de/2003-12-10-wimpy-wiki/"}}},pathContext:{slug:"/2003-12-10-wimpy-wiki/",prev:{fields:{title:"My further adventures with BackupPC",date:"10 Dec 2003",slug:"/2003-12-10-my-further-adventures-with-backuppc/"}},next:{fields:{title:"You blog, don't you?",date:"10 Dec 2003",slug:"/2003-12-10-you-blog-dont-you/"}}}}}});
