webpackJsonp([0x6099849126d2],{7106:function(t,a){t.exports={data:{post:{html:"<p>Last night it occurred to me that a country where they let little children run around in the dark with lit candles in paper lanterns, where they put the same lit candles in dried-out pine trees indoors for Christmas, and where they have a holiday that combines alcohol and hand-held fireworks, that such a country has a lot of gall calling <em>Americans</em> stupid for electing Bush.</p>",fields:{title:"A Nation of Pyromaniacs",date:"09 Nov 2004",url:"https://papascott.de/2004-11-09-a-nation-of-pyromaniacs/"}}},pathContext:{slug:"/2004-11-09-a-nation-of-pyromaniacs/",prev:{fields:{title:"Happy Cadaver Day",date:"08 Nov 2004",slug:"/2004-11-08-happy-cadaver-day/"}},next:{fields:{title:"",date:"09 Nov 2004",slug:"/2004-11-09-deutsche-nationale-regierungsumsturzenstag/"}}}}}});
